import React, { useEffect, useState } from 'react';
import ChartComponent from './ChartComponent';
 

function Table() {
  const [column, setColumn] = useState([]);
  const [records, setRecords] = useState([]);
  const [selectedRow, setSelectedRow] = useState(null);

  useEffect(() => {
    fetch('https://api.mfapi.in/mf/100049')
      .then((res) => res.json())
      .then((obj) => {
        setColumn(Object.keys(obj.data[0]));
        setRecords(obj.data);
      });
  }, []);

  const chartData = {
    labels: records.map((record) => record.date),
    datasets: [
      {
        label: 'NAV Values',
        data: records.map((record) => record.nav),
        backgroundColor: 'rgba(75,192,192,0.2)',
        borderColor: 'rgba(75,192,192,1)',
        borderWidth: 1,
      },
    ],
  };

  const handleRowClick = (row) => {
    setSelectedRow(row);
  };

  return (
    <div>
      <table className="tables">
        <thead>
          <tr>
            {column.map((c, i) => (
              <th key={i}>{c}</th>
            ))}
            <th>NAV Link</th>
          </tr>
        </thead>
        <tbody>
          {records.map((record, i) => (
            <tr key={i} onClick={() => handleRowClick(record)}>
              <td>{record.date}</td>
              <td>{record.nav}</td>
              <td>{'https://api.mfapi.in/mf/100049'}</td>
            </tr>
          ))}
        </tbody>
      </table>
      
      {selectedRow && <ChartComponent data={chartData} />}
    </div>
  );
}

export default Table;




